@app.route('/')
def index():
    return render_template('index.html')


@app.route('/login/user', methods=['POST'])
def login():
    usuario = request.args.get('usuario')
    senha = request.args.get('senha')
    if usuario:
        auth = authenticate(usuario, senha)
        return auth
    
    


