import os
import sqlite3

path = os.path.join(os.path.dirname(
    os.path.abspath(__file__)), 'fc.db')

conn = sqlite3.connect(path)
cursor = conn.cursor()

cursor.execute("""
INSERT INTO item (nome, descricao, imagem, preco, categoria)
VALUES (
    'notebook',
    'windows',
    'data:image/jpeg;base64,',
    '1900.00',
    'informatica'
)
""")

conn.commit()


conn.close()
